%% Initializationclear ; close all; clcaddpath('./libsvm-3.22/matlab');%% ==================== Part 1: iris SVM ====================%% 加载数据data = load('iris.txt');gamma = 0.21;X = data(:, 1:4);y = data(:, 5);% 样本数numInst = size(X,1);% 类别数numLabels = max(y);  rand_indices = randperm(numInst);train_x = X(rand_indices(1:100), :);train_y = y(rand_indices(1:100), :);test_x = X(rand_indices(101:150), :);test_y = y(rand_indices(101:150), :);svmStruct=svmtrain(train_y,train_x,sprintf('-s 0 -t 2 -c 1.2 -g %g', gamma))[predict_label, accuracy, dec_values] =svmpredict(test_y, test_x, svmStruct)