%% Initializationclear ; close all; clcaddpath('./libsvm-3.22/matlab');%% ==================== Part 1: linear SVM ====================%% 加载数据[train_y, train_x] = libsvmread('email_train-all.txt');%%  获取大小[m,n] = size(train_x);b = [train_x train_y];csvwrite('train.csv',b);model = svmtrain(train_y, train_x, sprintf('-s 0 -t 0 -c 4'));[test_y, test_x] = libsvmread('email_test.txt');c = [test_x test_y];[predicted_label, accuracy, decision_values] = svmpredict(test_y, test_x, model);csvwrite('test.csv',c);